import styled from 'styled-components'

const Container = styled.div`
  height: 100vh;
  width: 100%;
  display: grid;

  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 1fr 60px;
  grid-template-areas: 'image search' 'footer footer';
`

const ImageContainer = styled.div`
  grid-area: image;
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 100px 1fr;
  grid-template-areas: 'navbar' 'empty';
  background: url('https://images8.alphacoders.com/818/thumb-1920-818978.png');
  background-size: cover;
  background-repeat: no-repeat;
  background-position-x: start;
  background-position-y: center;
`

const FormContainer = styled.div`
  grid-area: search;
  height: 100%;
  width: 100%;
  background-color: var(--orange);
  padding: 50px;
  box-sizing: border-box;

  display: flex;
  flex-direction: column;
`

const FormTitle = styled.span`
  font-size: 62px;
  color: white;

  ${props => props.purple && `
    color: var(--purple);
    margin: 15px 0;
  `}
`

const Form = styled.div`
  width: 100%;
  height: 500px;
  background-color: #252525;
  justify-self: flex-end;

  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 100px 1fr;
  grid-template-areas: 'selector' 'input';
  border-radius: 5px;
  border: 1px solid white;
  overflow: hidden;
`

const FormSelector = styled.div`
  grid-area: selector;
  width: 100%;
  height: 100%;
  background-color: var(--purple);
  border-bottom: 1px solid white;

  display: grid;
  grid-template-columns: 100px 100px 1fr;
  grid-template-rows: 1fr;
  grid-gap: 1px;
  grid-template-areas: 'psn xbox steam';
  box-sizing: border-box;
`

const FormPlatform = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;

  & img {
    height: 80px;
    width: 80px;
  }

  ${props => props.psn && `
    grid-area: psn;
    background-color: var(--purple);
    border-right: 1px solid white;
  `}
  ${props => props.xbox && `
    grid-area: xbox;
    background-color: var(--green);
    border-right: 1px solid white;
  `}
  ${props => props.steam && `
    grid-area: steam;
    background-color: #252525;
  `}
`

export { FormContainer, ImageContainer, Container, FormTitle, Form, FormSelector, FormPlatform }
