import styled from 'styled-components'

const Container = styled.div`
  height: 100vh;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: 100px 1fr;
  grid-template-areas: 'navbar' 'view';
` 

const View = styled.div`
  grid-area: view;
  height: 100%;
  width: 100%;
  background: linear-gradient(rgba(0, 0, 0, .5), rgba(0, 0, 0, .5)), url('https://hdqwalls.com/download/pubg-poster-art-4k-gb-2560x1700.jpg');
  background-repeat: no-repeat;
  background-size: cover;
  background-position: end;
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  box-sizing: border-box;
  padding: 30px;
`

const PlayerHeader = styled.div`
  width: 100%;
  background-color: #111;
  box-sizing: border-box;
  padding: 20px;
  display: flex;
  justify-content: space-between;
`

const PlayerName = styled.span`
  color: white;
  font-weight: 400;
  font-size: 32px;
`

const PlayerDropdown = styled.div`
  color: white;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  font-size: 32px;

  cursor: pointer;
  user-select: none;

  & i {
    font-size: 22px;
    margin: 0 0 5px 5px;

    @media only screen and (max-width: 1400px) {
      font-size: 18px;
    }
  }
`

const PlayerDropdownContainer = styled.div`
  display: flex;
  flex-direction: column-reverse;
`

const PlayerDropdownBox = styled.div`
  width: 159px;
  height: max-content;
  display: none;
  position: absolute;
  margin-top: 50px;
  user-select: none;
  transform: translateY(100%);

  ${props =>
    props.show &&
    `
    display: block; 
    transform: translateY(0);
  `}
`

const PlayerDropdownOption = styled.div`
  display: flex;
  align-items: center;
  padding: 5px 10px;
  padding-bottom: 1px solid rgba(0, 0, 0, 0.25);
  cursor: pointer;

  &:hover {
    background-color: #252525;
  }
`

export {Container, View, PlayerHeader, PlayerDropdown, PlayerName, PlayerDropdownContainer, PlayerDropdownBox, PlayerDropdownOption}
