import React, { useEffect, useState } from 'react'
import axios from 'axios'
import NavigatorSolid from '../components/NavigatorSolid'
import { Container, View, PlayerHeader, PlayerName, ModeSelector, ModeOption, PlayerStats } from './PlayerStyles'

export default function Player(props) {
  const [notFound, setNotFound] = useState(false)
  const [user, setUser] = useState()
  const [loaded, setLoaded] = useState()
  const { username, platform } = props.match.params

  const handleClick = path => {
    props.history.push(`/leaderboards/${path}`)
  }

  useEffect(() => {
    ;(async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/players', {
          platform,
          username
        })
        console.log(res.data.attributes)
        setUser(res.data.attributes)
        setLoaded(true)
      } catch (err) {
        setNotFound(true)
        setLoaded(true)
      }
    })()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])
  if (loaded) {
    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View>
          {notFound ? (
            <>
              <h1>404 Player not found</h1>
            </>
          ) : (
            <>
              <PlayerHeader>
                <PlayerName>{user.name}</PlayerName>
                <ModeSelector>
                  <ModeOption active>SOLO</ModeOption>
                  <ModeOption>SOLO FPP</ModeOption>
                  <ModeOption>DUO</ModeOption>
                  <ModeOption>DUO FPP</ModeOption>
                  <ModeOption>SQUAD</ModeOption>
                  <ModeOption>SQUAD FPP</ModeOption>
                </ModeSelector>
              </PlayerHeader>
              <PlayerStats>
                Teste
              </PlayerStats>
            </>
          )}
        </View>
      </Container>
    )
  } else {
    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View />
      </Container>
    )
  }
}
