import React, { useEffect, useState } from 'react'
import axios from 'axios'
import NavigatorSolid from '../components/NavigatorSolid'
import Footer from '../components/Footer'
import {
  Container,
  View,
  PlayerHeader,
  PlayerName,
  ModeSelector,
  ModeOption,
  PlayerStats,
  PlayerHistory
} from './PlayerStyles'

export default function Player(props) {
  const [notFound, setNotFound] = useState(false)
  const [user, setUser] = useState()
  const [loaded, setLoaded] = useState()
  const [gamemode, setGamemode] = useState('solo')

  const { username, platform } = props.match.params

  const handleClick = path => {
    props.history.push(`/leaderboards/${path}`)
  }

  useEffect(() => {
    ;(async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/players', {
          platform,
          username
        })
        console.log(res.data.attributes)
        setUser(res.data.attributes)
        setLoaded(true)
      } catch (err) {
        setNotFound(true)
        setLoaded(true)
      }
    })()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  const handleGamemodeChange = e => {
    setGamemode(e)
  }

  if (loaded) {
    const stats = [
      'assists',
      'kills',
      'dailyKills',
      'weeklyKills',
      'teamKills',
      'headshotKills',
      'wins',
      'dailyWins',
      'weeklyWins',
      'losses',
      'top10s',
      'roundsPlayed'
    ]

    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View>
          {notFound ? (
            <>
              <h1>404 Player not found</h1>
            </>
          ) : (
            <>
              <PlayerHeader>
                <PlayerName>{user.name}</PlayerName>
                <ModeSelector>
                  <ModeOption
                    active={gamemode === 'solo'}
                    onClick={() => handleGamemodeChange('solo')}>
                    SOLO
                  </ModeOption>
                  <ModeOption
                    active={gamemode === 'solo-fpp'}
                    onClick={() => handleGamemodeChange('solo-fpp')}>
                    SOLO FPP
                  </ModeOption>
                  <ModeOption
                    active={gamemode === 'duo'}
                    onClick={() => handleGamemodeChange('duo')}>
                    DUO
                  </ModeOption>
                  <ModeOption
                    active={gamemode === 'duo-fpp'}
                    onClick={() => handleGamemodeChange('duo-fpp')}>
                    DUO FPP
                  </ModeOption>
                  <ModeOption
                    active={gamemode === 'squad'}
                    onClick={() => handleGamemodeChange('squad')}>
                    SQUAD
                  </ModeOption>
                  <ModeOption
                    active={gamemode === 'squad-fpp'}
                    onClick={() => handleGamemodeChange('squad-fpp')}>
                    SQUAD FPP
                  </ModeOption>
                </ModeSelector>
              </PlayerHeader>
              <PlayerStats>
                {stats.map(stat => (
                  <PlayerHistory key={Math.random()}>
                    <span>{stat}</span>
                    <span>
                      {user.stats.attributes.gameModeStats[gamemode][stat]}
                    </span>
                  </PlayerHistory>
                ))}
              </PlayerStats>
            </>
          )}
        </View>
        <Footer />
      </Container>
    )
  } else {
    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View>
          <div className="loading" />
        </View>
        <Footer />
      </Container>
    )
  }
}
