import React, { useEffect, useState } from 'react'
import axios from 'axios'
import NavigatorSolid from '../components/NavigatorSolid'
import { Container, View, PlayerHeader, PlayerDropdown, PlayerName, PlayerDropdownContainer, PlayerDropdownBox } from './PlayerStyles'

export default function Player(props) {
  const [notFound, setNotFound] = useState(false)
  const [user, setUser] = useState()
  const [loaded, setLoaded] = useState()
  const { username, platform } = props.match.params

  const handleClick = path => {
    props.history.push(`/leaderboards/${path}`)
  }

  useEffect(() => {
    ;(async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/players', {
          platform,
          username
        })
        console.log(res.data.attributes)
        setUser(res.data.attributes)
        setLoaded(true)
      } catch (err) {
        setNotFound(true)
        setLoaded(true)
      }
    })()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])
  if (loaded) {
    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View>
          {notFound ? (
            <>
              <h1>404 Player not found</h1>
            </>
          ) : (
            <PlayerHeader>
              <PlayerName>{user.name}</PlayerName>
              <PlayerDropdownContainer>
                <PlayerDropdown>
                  Duo
                  <i className="fas fa-caret-down"></i>
                </PlayerDropdown>
                <PlayerDropdownBox show={toggle}>
                  <DropdownOption onClick={() => handleClick('duo')}>
                    Duo
                  </DropdownOption>
                  <DropdownOption onClick={() => handleClick('duo-fpp')}>
                    Duo FPP
                  </DropdownOption>
                  <DropdownOption onClick={() => handleClick('solo')}>
                    Solo
                  </DropdownOption>
                  <DropdownOption onClick={() => handleClick('solo-fpp')}>
                    Solo FPP
                  </DropdownOption>
                  <DropdownOption onClick={() => handleClick('squad')}>
                    Squad
                  </DropdownOption>
                  <DropdownOption onClick={() => handleClick('squad-fpp')}>
                    Squad FPP
                  </DropdownOption>
                </PlayerDropdownBox>
              </PlayerDropdownContainer>
            </PlayerHeader>
          )}
        </View>
      </Container>
    )
  } else {
    return (
      <Container>
        <NavigatorSolid solid={true} handleClick={handleClick} />
        <View />
      </Container>
    )
  }
}
