import React, { useEffect, useState } from 'react'
import axios from 'axios'

import Navigator from '../components/NavigatorSolid'
import Footer from '../components/Footer'
import {
  Container,
  View,
  PlayerHeader,
  PlayerName,
  ModeSelector,
  ModeOption,
  PlayerStats,
  PlayerHistory
} from './LeaderboardsStyles'

export default function Leaderboards(props) {
  let gameMode = props.match.params.game_mode
  let [leaderboards, setLeaderboards] = useState([])
  let [loaded, setLoaded] = useState(false)

  const handleClick = path => {
    props.history.push(`/leaderboards/${path}`)
  }

  useEffect(() => {
    setLoaded(false)
    ;(async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/leaderboards', {
          game_mode: gameMode
        })
        console.log(res.data)
        setLeaderboards(res.data)
        setLoaded(true)
      } catch (err) {
        setLoaded(true)
      }
    })()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [gameMode])

  return (
    <Container>
      <Navigator handleClick={handleClick}/>
      <View>
        <PlayerHeader>
          <PlayerName>Leaderboards</PlayerName>
          <ModeSelector>
            <ModeOption
              active={gameMode === 'solo'}
              onClick={() => handleClick('solo')}>
              SOLO
            </ModeOption>
            <ModeOption
              active={gameMode === 'solo-fpp'}
              onClick={() => handleClick('solo-fpp')}>
              SOLO FPP
            </ModeOption>
            <ModeOption
              active={gameMode === 'duo'}
              onClick={() => handleClick('duo')}>
              DUO
            </ModeOption>
            <ModeOption
              active={gameMode === 'duo-fpp'}
              onClick={() => handleClick('duo-fpp')}>
              DUO FPP
            </ModeOption>
            <ModeOption
              active={gameMode === 'squad'}
              onClick={() => handleClick('squad')}>
              SQUAD
            </ModeOption>
            <ModeOption
              active={gameMode === 'squad-fpp'}
              onClick={() => handleClick('squad-fpp')}>
              SQUAD FPP
            </ModeOption>
          </ModeSelector>
        </PlayerHeader>
        <PlayerStats>
          {loaded ? (
            <>
            {leaderboards.sort((a, b) => (a.attributes.rank < b.attributes.rank) ? 1 : -1).map(user => (
              <PlayerHistory key={Math.random()}>
                <span>{user.attributes.name}</span>
                <span>
                  {user.attributes.rank}
                </span>
              </PlayerHistory>
            ))}
          </>
          ) : (
              <div className="loading"></div>
          )}
        </PlayerStats>
      </View>
      <Footer />
    </Container>
  )
}
